	jsr EchoInit
	move.l #0x19c80000, Entity_WorldPosX(a0)
	move.l #0x05c80000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L3A3_Echo_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0x9, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr EchoLoadGfx
