L3A3_Leaf_count equ 0x0

L3A3_Nymn_count equ 0x1
L3A3_Nymn_1_idx	equ 0x0

L3A3_Echo_count equ 0x0

L3A3_BouncePlant_count equ 0x0

L3A3_Boulder_count equ 0x0

L3A3_Monster_count equ 0x0

