	jsr EchoInit
	move.l #0x01180000, Entity_WorldPosX(a0)
	move.l #0x02180000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L4A1_Echo_2_name, a3
	move.l a0, a2
