L4A2_Leaf_count equ 0x0

L4A2_Echo_count equ 0x0

L4A2_Nymn_count equ 0x1
L4A2_Nymn_1_idx	equ 0x0

	IFND FINAL
L4A2_Nymn_1_name	dc.b "L4A2_Nymn_1",0
	even
	ENDIF

L4A2_BouncePlant_count equ 0x0

