L4A2_LoadGameObjects:


	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(Nymn_Struct_Size*L4A2_Nymn_count)
	move.l a1, EntityPoolStart_Nymn
	move.l a1, a0
	POPL   a1

	jsr NymnInit
	move.l #0x00b80000, Entity_WorldPosX(a0)
	move.l #0x04e00000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L4A2_Nymn_1_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0xb, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr NymnLoadGfx
	add.l #Nymn_Struct_Size, a0




























	rts

