L5A3_Leaf_count equ 0x0

L5A3_Djubb_count equ 0x0

L5A3_Echo_count equ 0x1
L5A3_Echo_4_idx	equ 0x0

	IFND FINAL
L5A3_Echo_4_name	dc.b "L5A3_Echo_4",0
	even
	ENDIF

