L5A3_LoadGameObjects:


	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(Echo_Struct_Size*L5A3_Echo_count)
	move.l a1, EntityPoolStart_Echo
	move.l a1, a0
	POPL   a1

	jsr EchoInit
	move.l #0x00a00000, Entity_WorldPosX(a0)
	move.l #0x03380000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L5A3_Echo_4_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0xb, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr EchoLoadGfx
	add.l #Echo_Struct_Size, a0

	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(Nymn_Struct_Size*L5A3_Nymn_count)
	move.l a1, EntityPoolStart_Nymn
	move.l a1, a0
	POPL   a1

	jsr NymnInit
	move.l #0x00b80000, Entity_WorldPosX(a0)
	move.l #0x03380000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L5A3_Nymn_3_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0xb, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr NymnLoadGfx
	add.l #Nymn_Struct_Size, a0


	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(Log_Struct_Size*L5A3_Log_count)
	move.l a1, EntityPoolStart_Log
	move.l a1, a0
	POPL   a1

	jsr LogInit
	move.l #0x12e00000, Entity_WorldPosX(a0)
	move.l #0x03d80000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L5A3_Log_1_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0xa, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr LogLoadGfx
	add.l #Log_Struct_Size, a0
























	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(Pillar_Struct_Size*L5A3_Pillar_count)
	move.l a1, EntityPoolStart_Pillar
	move.l a1, a0
	POPL   a1

	jsr PillarInit
	move.l #0x10c80000, Entity_WorldPosX(a0)
	move.l #0x04f00000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L5A3_Pillar_2_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0xd, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr PillarLoadGfx
	add.l #Pillar_Struct_Size, a0









	rts

