L5A4_LoadGameObjects:



	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(Echo_Struct_Size*L5A4_Echo_count)
	move.l a1, EntityPoolStart_Echo
	move.l a1, a0
	POPL   a1

	jsr EchoInit
	move.l #0x01e00000, Entity_WorldPosX(a0)
	move.l #0x03380000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L5A4_Echo_2_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0xb, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr EchoLoadGfx
	add.l #Echo_Struct_Size, a0

	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(Nymn_Struct_Size*L5A4_Nymn_count)
	move.l a1, EntityPoolStart_Nymn
	move.l a1, a0
	POPL   a1

	jsr NymnInit
	move.l #0x01f80000, Entity_WorldPosX(a0)
	move.l #0x03380000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L5A4_Nymn_1_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0xb, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr NymnLoadGfx
	add.l #Nymn_Struct_Size, a0











	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(Demon_Struct_Size*L5A4_Demon_count)
	move.l a1, EntityPoolStart_Demon
	move.l a1, a0
	POPL   a1

	jsr DemonInit
	move.l #0x01680000, Entity_WorldPosX(a0)
	move.l #0x03900000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L5A4_Demon_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0xa, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr DemonLoadGfx
	add.l #Demon_Struct_Size, a0




	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(TriggerBox_Struct_Size*L5A4_TriggerBox_count)
	move.l a1, EntityPoolStart_TriggerBox
	move.l a1, a0
	POPL   a1

	jsr TriggerBoxInit
	move.l #0x09e00000, Entity_WorldPosX(a0)
	move.l #0x02100000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L5A4_Trigger_Cutscene_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0x10, d0
	MEMCPYB a2,a3,d0
	ENDIF
	move.l #l5a4_Cutscene_Begin, TriggerBox_Routine(a0)
	move.b #0x1, TriggerBox_TriggerOnce(a0)
	move.b #0x1, TriggerBox_XOnly(a0)
	jsr TriggerBoxLoadGfx
	add.l #TriggerBox_Struct_Size, a0
	jsr TriggerBoxInit
	move.l #0x1f400000, Entity_WorldPosX(a0)
	move.l #0x00980000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L5A4_TriggerBox_LevelEnd_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0x10, d0
	MEMCPYB a2,a3,d0
	ENDIF
	move.l #l5a4_Trigger_End, TriggerBox_Routine(a0)
	move.b #0x1, TriggerBox_TriggerOnce(a0)
	move.b #0x1, TriggerBox_XOnly(a0)
	jsr TriggerBoxLoadGfx
	add.l #TriggerBox_Struct_Size, a0
	jsr TriggerBoxInit
	move.l #0x18680000, Entity_WorldPosX(a0)
	move.l #0x02080000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L5A4_TriggerBox_Storm_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0x10, d0
	MEMCPYB a2,a3,d0
	ENDIF
	move.l #l5a4_Trigger_Storm, TriggerBox_Routine(a0)
	move.b #0x1, TriggerBox_TriggerOnce(a0)
	move.b #0x1, TriggerBox_XOnly(a0)
	jsr TriggerBoxLoadGfx
	add.l #TriggerBox_Struct_Size, a0



	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(Location_Struct_Size*L5A4_Location_count)
	move.l a1, EntityPoolStart_Location
	move.l a1, a0
	POPL   a1

	jsr LocationInit
	move.l #0x02000000, Entity_WorldPosX(a0)
	move.l #0x02480000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L5A4_LocationCargoLiftStop_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0x10, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr LocationLoadGfx
	add.l #Location_Struct_Size, a0


















	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(CargoLift_Struct_Size*L5A4_CargoLift_count)
	move.l a1, EntityPoolStart_CargoLift
	move.l a1, a0
	POPL   a1

	jsr CargoLiftInit
	move.l #0x01e00000, Entity_WorldPosX(a0)
	move.l #0x03000000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L5A4_CargoLift_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0xe, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr CargoLiftLoadGfx
	add.l #CargoLift_Struct_Size, a0


	rts

