L5A4_LoadGameObjects:


	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(Echo_Struct_Size*L5A4_Echo_count)
	move.l a1, EntityPoolStart_Echo
	move.l a1, a0
	POPL   a1

	jsr EchoInit
	move.l #0x02600000, Entity_WorldPosX(a0)
	move.l #0x03500000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L5A4_Echo_2_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0xb, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr EchoLoadGfx
	add.l #Echo_Struct_Size, a0

	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(Nymn_Struct_Size*L5A4_Nymn_count)
	move.l a1, EntityPoolStart_Nymn
	move.l a1, a0
	POPL   a1

	jsr NymnInit
	move.l #0x02800000, Entity_WorldPosX(a0)
	move.l #0x03500000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L5A4_Nymn_1_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0xb, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr NymnLoadGfx
	add.l #Nymn_Struct_Size, a0

































	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(Demon_Struct_Size*L5A4_Demon_count)
	move.l a1, EntityPoolStart_Demon
	move.l a1, a0
	POPL   a1

	jsr DemonInit
	move.l #0x05480000, Entity_WorldPosX(a0)
	move.l #0x02e00000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L5A4_Demon_3_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0xc, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr DemonLoadGfx
	add.l #Demon_Struct_Size, a0

	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(Borgus_Struct_Size*L5A4_Borgus_count)
	move.l a1, EntityPoolStart_Borgus
	move.l a1, a0
	POPL   a1

	jsr BorgusInit
	move.l #0x03c80000, Entity_WorldPosX(a0)
	move.l #0x02d00000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L5A4_Borgus_4_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0xd, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr BorgusLoadGfx
	add.l #Borgus_Struct_Size, a0

	rts

