L7A1_Leaf_count equ 0x0

L7A1_Echo_count equ 0x0

L7A1_Nymn_count equ 0x1
L7A1_Nymn_1_idx	equ 0x0

	IFND FINAL
L7A1_Nymn_1_name	dc.b "L7A1_Nymn_1",0
	even
	ENDIF

