	IFND FINAL
L7A1_Wyrm_4_name	dc.b "L7A1_Wyrm_4",0
	even
	ENDIF


	IF L7A1_Leaf_count>Leaf_MaxEntities
	inform 2,"Entity array overflow(Leaf) - array size: 0x%d count : 0x%d",Leaf_MaxEntities,L7A1_Leaf_count
	ENDIF

