L7A1_LoadGameObjects:
	move.l #EntityArray_Leaf, a0

	move.l #EntityArray_Echo, a0

	move.l #EntityArray_Nymn, a0
	jsr NymnInit
	move.l #0x00b00000, Entity_WorldPosX(a0)
	move.l #0x02c80000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L7A1_Nymn_1_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0xb, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr NymnLoadGfx
	add.l #Nymn_Struct_Size, a0

	move.l #EntityArray_BouncePlant, a0

	move.l #EntityArray_Log, a0

	move.l #EntityArray_DjakkCage, a0

	move.l #EntityArray_Monster, a0

	move.l #EntityArray_Boulder, a0

	move.l #EntityArray_Firefly, a0

	move.l #EntityArray_FuzzlCage, a0

	move.l #EntityArray_Fuzzl, a0

	move.l #EntityArray_Stone, a0

	move.l #EntityArray_Nest, a0

	move.l #EntityArray_Flue, a0
	jsr FlueInit
	move.l #0x1bb80000, Entity_WorldPosX(a0)
	move.l #0x03980000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L7A1_Flue_2_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0xb, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr FlueLoadGfx
	add.l #Flue_Struct_Size, a0
	jsr FlueInit
	move.l #0x19b80000, Entity_WorldPosX(a0)
	move.l #0x03680000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L7A1_Flue_3_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0xb, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr FlueLoadGfx
	add.l #Flue_Struct_Size, a0

	move.l #EntityArray_TriggerBox, a0

	move.l #EntityArray_Checkpoint, a0

	move.l #EntityArray_WaterBody, a0

	move.l #EntityArray_Location, a0

	move.l #EntityArray_Deathbox, a0

	move.l #EntityArray_Lift, a0

	move.l #EntityArray_Crate, a0

	move.l #EntityArray_Generator, a0

	move.l #EntityArray_Cogs, a0

	move.l #EntityArray_Bridge, a0

	move.l #EntityArray_Button, a0

	move.l #EntityArray_Wyrm, a0
	jsr WyrmInit
	move.l #0x04a80000, Entity_WorldPosX(a0)
	move.l #0x03280000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L7A1_Wyrm_4_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0xb, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr WyrmLoadGfx
	add.l #Wyrm_Struct_Size, a0

	rts

