L7A2_LoadGameObjects:


	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(Echo_Struct_Size*L7A2_Echo_count)
	move.l a1, EntityPoolStart_Echo
	move.l a1, a0
	POPL   a1

	jsr EchoInit
	move.l #0x00e80000, Entity_WorldPosX(a0)
	move.l #0x02000000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L7A2_Echo_1_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0xb, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr EchoLoadGfx
	add.l #Echo_Struct_Size, a0









	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(Fuzzl_Struct_Size*L7A2_Fuzzl_count)
	move.l a1, EntityPoolStart_Fuzzl
	move.l a1, a0
	POPL   a1

	jsr FuzzlInit
	move.l #0x07280000, Entity_WorldPosX(a0)
	move.l #0x01400000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L7A2_FuzzlYellow1_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0x10, d0
	MEMCPYB a2,a3,d0
	ENDIF
	move.b #ColourYellow, Fuzzl_Colour(a0)
	jsr FuzzlLoadGfx
	add.l #Fuzzl_Struct_Size, a0
	jsr FuzzlInit
	move.l #0x0e900000, Entity_WorldPosX(a0)
	move.l #0x02200000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L7A2_FuzzlGreen1_name, a3
