L7A3_LoadGameObjects:






	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(Echo_Struct_Size*L7A3_Echo_count)
	move.l a1, EntityPoolStart_Echo
	move.l a1, a0
	POPL   a1

	jsr EchoInit
	move.l #0x00a00000, Entity_WorldPosX(a0)
	move.l #0x01280000, Entity_WorldPosY(a0)
	jsr EchoLoadGfx
	IFND FINAL
	lea L7A3_Echo_1_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0xb, d0
	MEMCPYB a2,a3,d0
	ENDIF
	add.l #Echo_Struct_Size, a0
























	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(Wyrm_Struct_Size*L7A3_Wyrm_count)
	move.l a1, EntityPoolStart_Wyrm
	move.l a1, a0
	POPL   a1

	jsr WyrmInit
	move.l #0x01a00000, Entity_WorldPosX(a0)
	move.l #0x01d00000, Entity_WorldPosY(a0)
	jsr WyrmLoadGfx
	IFND FINAL
	lea L7A3_Wyrm_2_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0xb, d0
	MEMCPYB a2,a3,d0
	ENDIF
	add.l #Wyrm_Struct_Size, a0



















	rts

