L8A2_Leaf_count equ 0x0

L8A2_Djubb_count equ 0x0

L8A2_Echo_count equ 0x1
L8A2_Echo_1_idx	equ 0x0

	IFND FINAL
L8A2_Echo_1_name	dc.b "L8A2_Echo_1",0
	even
	ENDIF

L8A2_Nymn_count equ 0x0

L8A2_BouncePlant_count equ 0x0

