L8A3_LoadGameObjects:


	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(Echo_Struct_Size*L8A3_Echo_count)
	move.l a1, EntityPoolStart_Echo
	move.l a1, a0
	POPL   a1

	jsr EchoInit
	move.l #0x06080000, Entity_WorldPosX(a0)
	move.l #0x00800000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L8A3_Echo_2_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0xb, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr EchoLoadGfx
	add.l #Echo_Struct_Size, a0













	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(TriggerBox_Struct_Size*L8A3_TriggerBox_count)
	move.l a1, EntityPoolStart_TriggerBox
	move.l a1, a0
	POPL   a1

	jsr TriggerBoxInit
	move.l #0x00c80000, Entity_WorldPosX(a0)
	move.l #0x00b00000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L8A3_TriggerLevelEnd_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0x10, d0
	MEMCPYB a2,a3,d0
	ENDIF
	move.l #l8a3_Trigger_End, TriggerBox_Routine(a0)
	move.b #0x1, TriggerBox_TriggerOnce(a0)
	jsr TriggerBoxLoadGfx
	add.l #TriggerBox_Struct_Size, a0
	jsr TriggerBoxInit
	move.l #0x00c80000, Entity_WorldPosX(a0)
	move.l #0x01800000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L8A3_TriggerStartLift_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0x10, d0
	MEMCPYB a2,a3,d0
	ENDIF
	move.l #l8a3_Trigger_Lift, TriggerBox_Routine(a0)
	move.b #0x1, TriggerBox_TriggerOnce(a0)
	jsr TriggerBoxLoadGfx
	add.l #TriggerBox_Struct_Size, a0





	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(Lift_Struct_Size*L8A3_Lift_count)
	move.l a1, EntityPoolStart_Lift
	move.l a1, a0
	POPL   a1

	jsr LiftInit
	move.l #0x00e00000, Entity_WorldPosX(a0)
	move.l #0x01e00000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L8A3_LiftExit_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0xd, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr LiftLoadGfx
	add.l #Lift_Struct_Size, a0















	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(Ladder_Struct_Size*L8A3_Ladder_count)
	move.l a1, EntityPoolStart_Ladder
	move.l a1, a0
	POPL   a1

	jsr LadderInit
	move.l #0x03a00000, Entity_WorldPosX(a0)
	move.l #0x02200000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L8A3_Ladder_1_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0xd, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr LadderLoadGfx
	add.l #Ladder_Struct_Size, a0

	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(Demon_Struct_Size*L8A3_Demon_count)
	move.l a1, EntityPoolStart_Demon
	move.l a1, a0
	POPL   a1

	jsr DemonInit
	move.l #0x01400000, Entity_WorldPosX(a0)
	move.l #0x01900000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L8A3_Demon_3_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0xc, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr DemonLoadGfx
	add.l #Demon_Struct_Size, a0


	rts

