L8A4_LoadGameObjects:



	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(Echo_Struct_Size*L8A4_Echo_count)
	move.l a1, EntityPoolStart_Echo
	move.l a1, a0
	POPL   a1

	jsr EchoInit
	move.l #0x09700000, Entity_WorldPosX(a0)
	move.l #0x02480000, Entity_WorldPosY(a0)
	jsr EchoLoadGfx
	IFND FINAL
	lea L8A4_Echo_2_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0xb, d0
	MEMCPYB a2,a3,d0
	ENDIF
	add.l #Echo_Struct_Size, a0











