L8A4_LoadGameObjects:


	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(Echo_Struct_Size*L8A4_Echo_count)
	move.l a1, EntityPoolStart_Echo
	move.l a1, a0
	POPL   a1

	jsr EchoInit
	move.l #0x09600000, Entity_WorldPosX(a0)
	move.l #0x02400000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L8A4_Echo_2_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0xb, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr EchoLoadGfx
	add.l #Echo_Struct_Size, a0













	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(TriggerBox_Struct_Size*L8A4_TriggerBox_count)
	move.l a1, EntityPoolStart_TriggerBox
	move.l a1, a0
	POPL   a1

	jsr TriggerBoxInit
	move.l #0x00800000, Entity_WorldPosX(a0)
	move.l #0x01880000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L8A4_TriggerLevelEnd_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0x10, d0
	MEMCPYB a2,a3,d0
	ENDIF
	move.l #l8a4_Trigger_End, TriggerBox_Routine(a0)
	move.b #0x1, TriggerBox_TriggerOnce(a0)
	move.b #0x1, TriggerBox_XOnly(a0)
	jsr TriggerBoxLoadGfx
	add.l #TriggerBox_Struct_Size, a0





	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(Lift_Struct_Size*L8A4_Lift_count)
	move.l a1, EntityPoolStart_Lift
	move.l a1, a0
	POPL   a1

	jsr LiftInit
	move.l #0x09600000, Entity_WorldPosX(a0)
	move.l #0x02600000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L8A4_LiftEntrance_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0x10, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr LiftLoadGfx
	add.l #Lift_Struct_Size, a0
















	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(Demon_Struct_Size*L8A4_Demon_count)
	move.l a1, EntityPoolStart_Demon
	move.l a1, a0
	POPL   a1

	jsr DemonInit
	move.l #0x01c80000, Entity_WorldPosX(a0)
	move.l #0x00f80000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L8A4_Demon_3_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0xc, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr DemonLoadGfx
	add.l #Demon_Struct_Size, a0


	rts

