L8A6_LoadGameObjects:


	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(Echo_Struct_Size*L8A6_Echo_count)
	move.l a1, EntityPoolStart_Echo
	move.l a1, a0
	POPL   a1

	jsr EchoInit
	move.l #0x09a00000, Entity_WorldPosX(a0)
	move.l #0x00f80000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L8A6_Echo_3_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0xb, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr EchoLoadGfx
	add.l #Echo_Struct_Size, a0


	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(BouncePlant_Struct_Size*L8A6_BouncePlant_count)
	move.l a1, EntityPoolStart_BouncePlant
	move.l a1, a0
	POPL   a1

	jsr BouncePlantInit
	move.l #0x05300000, Entity_WorldPosX(a0)
	move.l #0x02080000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L8A6_BouncePlant_2_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0x10, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr BouncePlantLoadGfx
	add.l #BouncePlant_Struct_Size, a0













	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(WaterBody_Struct_Size*L8A6_WaterBody_count)
	move.l a1, EntityPoolStart_WaterBody
	move.l a1, a0
	POPL   a1

	jsr WaterBodyInit
	move.l #0x05900000, Entity_WorldPosX(a0)
	move.l #0x02200000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L8A6_WaterBody_1_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0x10, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr WaterBodyLoadGfx
	add.l #WaterBody_Struct_Size, a0



















	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(Demon_Struct_Size*L8A6_Demon_count)
	move.l a1, EntityPoolStart_Demon
	move.l a1, a0
	POPL   a1

	jsr DemonInit
	move.l #0x01200000, Entity_WorldPosX(a0)
	move.l #0x01c80000, Entity_WorldPosY(a0)
	IFND FINAL
	lea L8A6_Demon_4_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0xc, d0
	MEMCPYB a2,a3,d0
	ENDIF
	jsr DemonLoadGfx
	add.l #Demon_Struct_Size, a0



	rts

