legal_screen_LoadGameObjects:












	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(Monster_Struct_Size*legal_screen_Monster_count)
	move.l a1, EntityPoolStart_Monster
	move.l a1, a0
	POPL   a1

	jsr MonsterInit
	move.l #0x00080000, Entity_WorldPosX(a0)
	move.l #0x00b80000, Entity_WorldPosY(a0)
	jsr MonsterLoadGfx
	IFND FINAL
	lea legal_screen_LegalScreenDjakk_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0x10, d0
	MEMCPYB a2,a3,d0
	ENDIF
	add.l #Monster_Struct_Size, a0




	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(Fuzzl_Struct_Size*legal_screen_Fuzzl_count)
	move.l a1, EntityPoolStart_Fuzzl
	move.l a1, a0
	POPL   a1

	jsr FuzzlInit
	move.l #0x01180000, Entity_WorldPosX(a0)
	move.l #0x00f00000, Entity_WorldPosY(a0)
	move.b #ColourYellow, Fuzzl_Colour(a0)
	jsr FuzzlLoadGfx
	IFND FINAL
	lea legal_screen_LegalScreenFuzzl_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0x10, d0
	MEMCPYB a2,a3,d0
	ENDIF
	add.l #Fuzzl_Struct_Size, a0

































	rts

