main_menu_LoadGameObjects:
	PUSHL  a1
	RAMPOOL_ALLOC Pool_Entities, #(Leaf_Struct_Size*main_menu_Leaf_count)
	move.l a1, EntityPoolStart_Leaf
	move.l a1, a0
	POPL   a1

	jsr LeafInit
	jsr LeafLoadGfx
	IFND FINAL
	lea main_menu_Leaf_3_name, a3
	move.l a0, a2
	add.l #Entity_DebugName, a2
	move.l #0x10, d0
