actor_cart:

sprite_cart_size_b		equ 0x0280	; Size in bytes
sprite_cart_size_t		equ 0x0014	; Size in tiles
sprite_cart_size_subsprites		equ 0x0002	; Size in subsprites
sprite_cart_widthheight_subsprites		equ 0x0201	; Width/height (bb) in subsprites

; Subsprite offsets from 0,0 (in pixels) - unflipped (ww) and flipped X (ww)
sprite_cart_subsprite_pos_offsets:
	dc.l 0x00000000, 0x00080000
	dc.l 0x00200000, 0x00000000
	Even

; Subsprite dimension bits (for sprite descs)
sprite_cart_subsprite_dimensions_bits:
	dc.b 0xF
	dc.b 0x3
	Even

; Num tiles per subsprite (for sprite descs)
sprite_cart_numtiles_per_subsprite:
	dc.b 0x10
	dc.b 0x04
	Even


spritesheet_cart_roll_frameoffset	equ 0x00	; Offset to first frame in sprite sheet
spritesheet_cart_roll_width		equ 0x28	; Width in pixels
spritesheet_cart_roll_height		equ 0x20	; Width in pixels
spritesheet_cart_roll_size_b		equ 0xA00	; Size in bytes
spritesheet_cart_roll_size_w		equ 0x500	; Size in words
spritesheet_cart_roll_size_l		equ 0x280	; Size in longwords
spritesheet_cart_roll_size_t		equ 0x50	; Size in tiles


