actor_checkpnt:

actor_checkpnt_VRAM_size_b		equ 0x280	; VRAM size to alloc (size of largest frame, bytes)
actor_checkpnt_sheet_inactive_width		equ 0x20	; Max width of largest frame in pixels
actor_checkpnt_sheet_inactive_height		equ 0x28	; Max height of largest frame in pixels


actor_checkpnt_sheet_active_width		equ 0x20	; Max width of largest frame in pixels
actor_checkpnt_sheet_active_height		equ 0x28	; Max height of largest frame in pixels


actor_checkpnt_sheet_inactive_frametable:
actor_checkpnt_sheet_inactive_frame_0_header:
	dc.w 0x0014	; Frame size (tiles)
	dc.w actor_checkpnt_sheet_inactive_frame_0_size_subsprites	; Frame size (subsprites)
	dc.l actor_checkpnt_sheet_inactive_frame_0
	dc.l actor_checkpnt_sheet_inactive_frame_0_subsprite_dimensions_bits
	dc.l actor_checkpnt_sheet_inactive_frame_0_subsprite_pos_offsets

actor_checkpnt_sheet_active_frametable:
actor_checkpnt_sheet_active_frame_0_header:
	dc.w 0x0014	; Frame size (tiles)
	dc.w actor_checkpnt_sheet_active_frame_0_size_subsprites	; Frame size (subsprites)
	dc.l actor_checkpnt_sheet_active_frame_0
	dc.l actor_checkpnt_sheet_active_frame_0_subsprite_dimensions_bits
	dc.l actor_checkpnt_sheet_active_frame_0_subsprite_pos_offsets

actor_checkpnt_sheet_inactive_frame_0_size_b		equ 0x0280	; Size in bytes
actor_checkpnt_sheet_inactive_frame_0_size_t		equ 0x0014	; Size in tiles
actor_checkpnt_sheet_inactive_frame_0_size_subsprites		equ 0x0002	; Size in subsprites

; Subsprite offsets from 0,0 (in pixels) - unflipped (ww) and flipped X (ww)
actor_checkpnt_sheet_inactive_frame_0_subsprite_pos_offsets:
	dc.l 0x00000000, 0x00000000
	dc.l 0x00000020, 0x00000020
	Even

; Subsprite dimension bits (for sprite descs)
actor_checkpnt_sheet_inactive_frame_0_subsprite_dimensions_bits:
	dc.b 0xF
	dc.b 0xC
	Even


actor_checkpnt_sheet_active_frame_0_size_b		equ 0x0280	; Size in bytes
actor_checkpnt_sheet_active_frame_0_size_t		equ 0x0014	; Size in tiles
actor_checkpnt_sheet_active_frame_0_size_subsprites		equ 0x0002	; Size in subsprites

; Subsprite offsets from 0,0 (in pixels) - unflipped (ww) and flipped X (ww)
actor_checkpnt_sheet_active_frame_0_subsprite_pos_offsets:
	dc.l 0x00000000, 0x00000000
	dc.l 0x00000020, 0x00000020
	Even

; Subsprite dimension bits (for sprite descs)
actor_checkpnt_sheet_active_frame_0_subsprite_dimensions_bits:
	dc.b 0xF
	dc.b 0xC
	Even


