actor_djrake_VRAM_size_b		equ 0xA00	; VRAM size to alloc (size of largest frame, bytes)
actor_djrake_sheet_idle_width		equ 0x80	; Max width of largest frame in pixels
actor_djrake_sheet_idle_height		equ 0x50	; Max height of largest frame in pixels


actor_djrake_sheet_idle_frametable:
actor_djrake_sheet_idle_frame_0_header:
	dc.w 0x0050	; Frame size (tiles)
