actor_dropbrig:

actor_dropbrig_VRAM_size_b		equ 0x40	; VRAM size to alloc (size of largest frame, bytes)
actor_dropbrig_sheet_notch_width		equ 0x10	; Max width of largest frame in pixels
actor_dropbrig_sheet_notch_height		equ 0x08	; Max height of largest frame in pixels


actor_dropbrig_sheet_notch_frametable:
actor_dropbrig_sheet_notch_frame_0_header:
	dc.w 0x0002	; Frame size (tiles)
	dc.w actor_dropbrig_sheet_notch_frame_0_size_subsprites	; Frame size (subsprites)
	dc.l actor_dropbrig_sheet_notch_frame_0
	dc.l actor_dropbrig_sheet_notch_frame_0_subsprite_dimensions_bits
	dc.l actor_dropbrig_sheet_notch_frame_0_subsprite_pos_offsets

actor_dropbrig_sheet_notch_frame_0_size_b		equ 0x0040	; Size in bytes
actor_dropbrig_sheet_notch_frame_0_size_t		equ 0x0002	; Size in tiles
actor_dropbrig_sheet_notch_frame_0_size_subsprites		equ 0x0001	; Size in subsprites

; Subsprite offsets from 0,0 (in pixels) - unflipped (ww) and flipped X (ww)
actor_dropbrig_sheet_notch_frame_0_subsprite_pos_offsets:
	dc.l 0x00000000, 0x00000000
	Even

; Subsprite dimension bits (for sprite descs)
actor_dropbrig_sheet_notch_frame_0_subsprite_dimensions_bits:
	dc.b 0x4
	Even


spritesheets_dropbrig:

spritesheet_dropbrig_notch:

actor_dropbrig_sheet_notch_frame_0:
	dc.l	0x55558585
	dc.l	0x55BEBEB8
	dc.l	0x858EE8EE
	dc.l	0x855BEEB8
	dc.l	0x0855BEEE
	dc.l	0x005558BB
	dc.l	0x00085555
	dc.l	0x00008855

	dc.l	0x58585555
	dc.l	0x8BEBEB55
	dc.l	0xEE8EE858
	dc.l	0x8BEEB558
	dc.l	0xEEEB5580
	dc.l	0xBB855500
	dc.l	0x55558000
	dc.l	0x55880000




