sprite_fuzlcage_subsprite_dimensions_bits:
	dc.b 0xF
	dc.b 0xC
	dc.b 0x7
	dc.b 0x4
	Even

; Num tiles per subsprite (for sprite descs)
sprite_fuzlcage_numtiles_per_subsprite:
	dc.b 0x10
	dc.b 0x04
	dc.b 0x08
	dc.b 0x02
	Even


