actor_mushroom_sheet_mushroom_180_height		equ 0x18	; Max height of largest frame in pixels
actor_mushroom_sheet_mushroom_180_VRAM_size_b		equ 0x120	; VRAM size to alloc (size of largest frame, bytes)


