actor_raft:

sprite_raft_size_b		equ 0x0140	; Size in bytes
sprite_raft_size_t		equ 0x000A	; Size in tiles
sprite_raft_size_subsprites		equ 0x0003	; Size in subsprites
sprite_raft_widthheight_subsprites		equ 0x0301	; Width/height (bb) in subsprites

; Subsprite offsets from 0,0 (in pixels) - unflipped (ww) and flipped X (ww)
sprite_raft_subsprite_pos_offsets:
	dc.l 0x00000000, 0x00300000
	dc.l 0x00200000, 0x00100000
	dc.l 0x00400000, 0x00000000
	Even

; Subsprite dimension bits (for sprite descs)
sprite_raft_subsprite_dimensions_bits:
	dc.b 0xC
	dc.b 0xC
	dc.b 0x4
	Even

; Num tiles per subsprite (for sprite descs)
sprite_raft_numtiles_per_subsprite:
	dc.b 0x04
	dc.b 0x04
	dc.b 0x02
	Even


spritesheet_raft_float_frameoffset	equ 0x00	; Offset to first frame in sprite sheet
spritesheet_raft_float_width		equ 0x50	; Width in pixels
spritesheet_raft_float_height		equ 0x08	; Width in pixels
spritesheet_raft_float_size_b		equ 0x140	; Size in bytes
spritesheet_raft_float_size_w		equ 0xA0	; Size in words
spritesheet_raft_float_size_l		equ 0x50	; Size in longwords
spritesheet_raft_float_size_t		equ 0x0A	; Size in tiles


