actor_raft:

actor_raft_VRAM_size_b		equ 0x140	; VRAM size to alloc (size of largest frame, bytes)
actor_raft_sheet_float_width		equ 0x50	; Max width of largest frame in pixels
actor_raft_sheet_float_height		equ 0x08	; Max height of largest frame in pixels


actor_raft_sheet_float_frametable:
actor_raft_sheet_float_frame_0_header:
	dc.w 0x000A	; Frame size (tiles)
	dc.w actor_raft_sheet_float_frame_0_size_subsprites	; Frame size (subsprites)
	dc.l actor_raft_sheet_float_frame_0
	dc.l actor_raft_sheet_float_frame_0_subsprite_dimensions_bits
	dc.l actor_raft_sheet_float_frame_0_subsprite_pos_offsets

actor_raft_sheet_float_frame_0_size_b		equ 0x0140	; Size in bytes
actor_raft_sheet_float_frame_0_size_t		equ 0x000A	; Size in tiles
actor_raft_sheet_float_frame_0_size_subsprites		equ 0x0003	; Size in subsprites

; Subsprite offsets from 0,0 (in pixels) - unflipped (ww) and flipped X (ww)
actor_raft_sheet_float_frame_0_subsprite_pos_offsets:
	dc.l 0x00000000, 0x00300000
	dc.l 0x00200000, 0x00100000
	dc.l 0x00400000, 0x00000000
	Even

; Subsprite dimension bits (for sprite descs)
actor_raft_sheet_float_frame_0_subsprite_dimensions_bits:
	dc.b 0xC
	dc.b 0xC
	dc.b 0x4
	Even


