actor_rain:

sprite_rain_size_b		equ 0x0200	; Size in bytes
sprite_rain_size_t		equ 0x0010	; Size in tiles
sprite_rain_size_subsprites		equ 0x0001	; Size in subsprites
sprite_rain_widthheight_subsprites		equ 0x0101	; Width/height (bb) in subsprites

; Subsprite offsets from 0,0 (in pixels) - unflipped (bb) and flipped X (bb)
sprite_rain_subsprite_pos_offsets:
	dc.w 0x0000, 0x0000
	Even

; Subsprite dimension bits (for sprite descs)
sprite_rain_subsprite_dimensions_bits:
	dc.b 0xF
	Even

; Num tiles per subsprite (for sprite descs)
sprite_rain_numtiles_per_subsprite:
	dc.b 0x10
	Even


spritesheet_rain_heavy_frameoffset	equ 0x00	; Offset to first frame in sprite sheet
spritesheet_rain_heavy_size_b		equ 0x200	; Size in bytes
spritesheet_rain_heavy_size_w		equ 0x100	; Size in words
spritesheet_rain_heavy_size_l		equ 0x80	; Size in longwords
spritesheet_rain_heavy_size_t		equ 0x10	; Size in tiles


