actor_rope:

actor_rope_VRAM_size_b		equ 0x180	; VRAM size to alloc (size of largest frame, bytes)
actor_rope_sheet_rope_l5_width		equ 0x38	; Max width of largest frame in pixels
actor_rope_sheet_rope_l5_height		equ 0x20	; Max height of largest frame in pixels


actor_rope_sheet_rope_l4_width		equ 0x38	; Max width of largest frame in pixels
actor_rope_sheet_rope_l4_height		equ 0x20	; Max height of largest frame in pixels


actor_rope_sheet_rope_width		equ 0x38	; Max width of largest frame in pixels
actor_rope_sheet_rope_height		equ 0x20	; Max height of largest frame in pixels


actor_rope_sheet_rope_l5_frametable:
actor_rope_sheet_rope_l5_frame_0_header:
	dc.w 0x000C	; Frame size (tiles)
	dc.w actor_rope_sheet_rope_l5_frame_0_size_subsprites	; Frame size (subsprites)
	dc.l actor_rope_sheet_rope_l5_frame_0
	dc.l actor_rope_sheet_rope_l5_frame_0_subsprite_dimensions_bits
	dc.l actor_rope_sheet_rope_l5_frame_0_subsprite_pos_offsets

actor_rope_sheet_rope_l4_frametable:
actor_rope_sheet_rope_l4_frame_0_header:
	dc.w 0x000C	; Frame size (tiles)
	dc.w actor_rope_sheet_rope_l4_frame_0_size_subsprites	; Frame size (subsprites)
	dc.l actor_rope_sheet_rope_l4_frame_0
	dc.l actor_rope_sheet_rope_l4_frame_0_subsprite_dimensions_bits
	dc.l actor_rope_sheet_rope_l4_frame_0_subsprite_pos_offsets

actor_rope_sheet_rope_frametable:
actor_rope_sheet_rope_frame_0_header:
	dc.w 0x000C	; Frame size (tiles)
	dc.w actor_rope_sheet_rope_frame_0_size_subsprites	; Frame size (subsprites)
	dc.l actor_rope_sheet_rope_frame_0
	dc.l actor_rope_sheet_rope_frame_0_subsprite_dimensions_bits
	dc.l actor_rope_sheet_rope_frame_0_subsprite_pos_offsets

actor_rope_sheet_rope_l5_frame_0_size_b		equ 0x0180	; Size in bytes
actor_rope_sheet_rope_l5_frame_0_size_t		equ 0x000C	; Size in tiles
actor_rope_sheet_rope_l5_frame_0_size_subsprites		equ 0x0001	; Size in subsprites

; Subsprite offsets from 0,0 (in pixels) - unflipped (ww) and flipped X (ww)
actor_rope_sheet_rope_l5_frame_0_subsprite_pos_offsets:
	dc.l 0x00100000, 0x00100000
	Even

; Subsprite dimension bits (for sprite descs)
actor_rope_sheet_rope_l5_frame_0_subsprite_dimensions_bits:
	dc.b 0xB
	Even


actor_rope_sheet_rope_l4_frame_0_size_b		equ 0x0180	; Size in bytes
actor_rope_sheet_rope_l4_frame_0_size_t		equ 0x000C	; Size in tiles
actor_rope_sheet_rope_l4_frame_0_size_subsprites		equ 0x0001	; Size in subsprites

; Subsprite offsets from 0,0 (in pixels) - unflipped (ww) and flipped X (ww)
actor_rope_sheet_rope_l4_frame_0_subsprite_pos_offsets:
	dc.l 0x00100000, 0x00100000
	Even

; Subsprite dimension bits (for sprite descs)
actor_rope_sheet_rope_l4_frame_0_subsprite_dimensions_bits:
	dc.b 0xB
	Even


actor_rope_sheet_rope_frame_0_size_b		equ 0x0180	; Size in bytes
actor_rope_sheet_rope_frame_0_size_t		equ 0x000C	; Size in tiles
actor_rope_sheet_rope_frame_0_size_subsprites		equ 0x0001	; Size in subsprites

; Subsprite offsets from 0,0 (in pixels) - unflipped (ww) and flipped X (ww)
actor_rope_sheet_rope_frame_0_subsprite_pos_offsets:
	dc.l 0x00100000, 0x00100000
	Even

; Subsprite dimension bits (for sprite descs)
actor_rope_sheet_rope_frame_0_subsprite_dimensions_bits:
	dc.b 0xB
	Even


